export default {
  // 設定画面にアクセス可能
  SETTINGS: 'settings',

  // Admin User のリストの取得が可能
  OBTAIN_USER_LIST: 'obtain-user-list',

  // Admin User のパスワード変更が可能
  CHANGE_PSWD: 'change-password',

  // List の閲覧・登録・更新・削除が可能
  LIST: 'list',

  // Group の全情報の閲覧・編集・削除が可能
  GROUP: 'group',

  // Group の個人情報以外の閲覧のみ可能
  GROUP_INF: 'group-inf',

  //  メールの作成・送信が可能
  MAIL: 'mail',

  //  送信メール履歴の閲覧が可能
  SENT_MAIL_HISTORY: 'sent-mail-history',

  //  送信メール履歴の閲覧が可能
  SENT_MAIL_HISTORY_INF: 'sent-mail-history-inf',

  // (not Admin) User 情報の閲覧・登録・更新・削除が可能
  USER: 'user',

  // メール送信設定の閲覧・変更が可能
  SEND_MAIL_SETTING: 'send-mail-setting',

  // メールテンプレートの閲覧・編集が可能
  MAIL_TEMPLATE: 'mail-template',

  // API Token の閲覧・生成が可能
  API_TOKEN: 'api-token'
};

